var NoteMdl = ottoman.model('Note', {
  user: { ref: 'User' },
  msg: 'string'
});

var UserMdl = ottoman.model('User', {
  name: 'string',
  note: NoteMdl
});

var user = new UserMdl();
var note = new NoteMdl();

note.user = user;
note.msg = 'Hey there!';

user.name = 'Brett';

user.save(function(err) {
  if (err) console.error(err);

  note.save(function(err) {
    if (err) console.error(err);

    console.log(user);
    console.log(note);

    NoteMdl.getById(note.id(), function(err, userX) {
      if (err) console.error(err);

      console.log('LOADED', userX);
      userX.load('user', function(err) {
        if (err) console.error(err);

        console.log('LOADED2', userX);
      })
    });
  });
});

return;

